    def upload_list(self):
        dao = cthdDAO()
        result = dao.ReadFromDatabase()
        self.fillTable(result)

    def fillTable(self, datas):
        row = 0
        self.table_dichvu.setRowCount(len(datas))
        for data in datas:
                service = serviceDAO().getServiceById(data.get_madv())
                self.table_dichvu.setItem(row, 0, QtWidgets.QTableWidgetItem(str(data.get_madv())))
                self.table_dichvu.setItem(row, 1, QtWidgets.QTableWidgetItem(service.getTen()))
                self.table_dichvu.setItem(row, 2, QtWidgets.QTableWidgetItem(str(data.get_soLuong())))
                self.table_dichvu.setItem(row, 3, QtWidgets.QTableWidgetItem(str(service.getGia())))
                row = row +1
    
    def loadComboboxDV(self):
        dao = serviceDAO()
        service = dao.getAllServices()
        for s in service:
            self.cbDichVu.addItem(f"{s.getTen()}")
    
    def loadPriceOfService(self, madv):
        dao = serviceDAO()
        service = dao.getServiceById(madv)
        return str(service.getGia())

    

